mapper.select:

select user_id, username, nickname, email, password, introduction 
from UserInfo
where user_id = #{user_id}

mapper.insert:
insert into UserInfo(
	username, nickname, email, password, introduction 
) values (
	#{username},#{nickname},#{email},#{password},#{introduction}
)

mapper.select:
select user_id, username, nickname, email, password, introduction 
from UserInfo
where email = #{email}

select user_id, username, nickname, email, password, introduction 
from UserInfo
where email = #{email}
and password = #{password}

select user_id, username, nickname, email, password, introduction 
from UserInfo
where nickname = #{nickname}
