mapper.insert:
insert into doctor(
user_id, doctorimg, doctorauth
) values (
#{user_id}, #{doctorimg}, #{doctorauth}
)

mapper.update:
update doctor
set doctorimg = #{doctorimg}
where user_id = #{user_id}

update doctor
set doctorimg = #{doctorimg}
where user_id = #{user_id}

update doctor
set doctorauth = "accept"
where user_id = #{user_id}

update doctor
set doctorauth = "fail"
where user_id = #{user_id}
