mapper.select:

select user_id, username, nickname, email, password, introduction, userimage, role, disabled
from UserInfo
where user_id = #{user_id}

mapper.insert:
insert into UserInfo(
	username, nickname, email, password, introduction, userimage
) values (
	#{username},#{nickname},#{email},#{password},#{introduction},#{userimage}
)

mapper.select:
select user_id, username, nickname, email, password, introduction, userimage, role, disabled
from UserInfo
where email = #{email}

select user_id, username, nickname, email, password, introduction, userimage, role, disabled
from UserInfo
where email = #{email}
and password = #{password}

select user_id, username, nickname, email, password, introduction, userimage, role, disabled
from UserInfo
where nickname = #{nickname}

select user_id
from UserInfo
where email = #{email}

select nickname
from UserInfo
where user_id=#{user_id}

mapper.update:
update	UserInfo
   set	userimage = #{userimage}
 where 	user_id = #{user_id}

update	UserInfo
   set	password = #{password}
 where 	user_id = #{user_id}
